mapper.select:
SELECT ROWNUM AS RNUM, A.* , (SELECT STORED_IMAGE_NAME FROM (SELECT * FROM UPLOAD_IMAGE ORDER BY IMAGE_NO) WHERE CLUB_BOARD_ID IN(A.CLUB_BOARD_ID) AND ROWNUM <= 1) AS IMAGE_STR FROM ( SELECT * FROM CLUB_BOARD WHERE CLUB_NAME LIKE '%' || #{clubName} || '%'
AND TITLE LIKE '%' || #{pageDto.keyword} || '%'
order by CREATE_DATE desc ) A WHERE ROWNUM BETWEEN #{pageDto.start} AND #{pageDto.end}
