mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY CREATE_DATE DESC) AS RNUM, A.*, UI.STORED_IMAGE_NAME AS IMAGE_STR -- UPLOAD_IMAGE 테이블과 조인하여 STORED_IMAGE_NAME 가져오기 FROM ( SELECT * FROM CLUB
AND CLUB_NAME LIKE '%' || #{keyword} || '%'
AND AREA LIKE '%' || #{keyword} || '%'
) A LEFT JOIN UPLOAD_IMAGE UI ON A.CLUB_NAME = UI.CLUB_NAME -- 조인 추가 ) WHERE RNUM BETWEEN #{start} AND #{end}

mapper.update:
UPDATE CLUB_USER SET
    IN_CK = 'Y'
WHERE USER_NO = #{userNo} AND CLUB_NAME = #{clubName}
