mapper.select:
SELECT * FROM ( SELECT o.ORDER_NO, o.PRODUCT_NO, p.TRADE_NO, o.CREATE_AT, t.TITLE, t.PRICE, o.RECIPIENT_NAME, o.state, ROW_NUMBER() OVER (ORDER BY o.ORDER_NO DESC) AS rnum FROM half_order o INNER JOIN half_product p ON o.PRODUCT_NO = p.PRODUCT_NO INNER JOIN trade_board t ON p.TRADE_NO = t.TRADE_NO INNER JOIN member m ON o.BUYER_MEMBER_NO = m.MEMBER_NO
AND m.ID LIKE '%' || #{searchCond.buyerMemberId} || '%'
AND m.phone_number LIKE '%' || #{searchCond.buyerPhone} || '%'
AND o.RECIPIENT_NAME LIKE '%' || #{searchCond.recipientName} || '%'
AND o.RECIPIENT_PHONE LIKE '%' || #{searchCond.recipientPhone} || '%'
) subquery WHERE rnum BETWEEN #{start} AND #{end}
