mapper.select:
SELECT *
FROM (
SELECT PRODUCT_NO, TRADE_NO, SELLER_MEMBER_NO, SELLER_MEMBER_ID, SELLER_PHONE, HALF_PRICE, IS_DONE, CREATE_AT, ADMIN_MEMO, ROW_NUMBER() OVER (ORDER BY PRODUCT_NO DESC) AS rnum
FROM half_product
) subquery
WHERE rnum BETWEEN #{start} AND #{end}

SELECT *
FROM (
SELECT p.PRODUCT_NO, p.TRADE_NO, p.SELLER_MEMBER_NO, p.SELLER_MEMBER_ID, p.SELLER_PHONE, p.HALF_PRICE, p.IS_DONE, p.CREATE_AT, p.ADMIN_MEMO,
       t.category_code, t.title ,ROW_NUMBER() OVER (ORDER BY p.PRODUCT_NO DESC) AS rnum
FROM half_product p
inner join trade_board t on p.TRADE_NO = t.TRADE_NO


        AND t.CATEGORY_CODE = #{productSearchCond.categoryCode}


        AND t.title = #{productSearchCond.title}

    and p.is_done = 1

) subquery
WHERE rnum BETWEEN #{start} AND #{end}

mapper.update:
update half_product
IS_DONE = #{isDone}, ADMIN_MEMO = #{adminMemo},
where PRODUCT_NO = #{productNo}
